import React, { PropTypes } from 'react';
import { View, StyleSheet, TouchableOpacity } from 'react-native';

const Col = (props) => {
  const {containerStyle, size, onPress, activeOpacity} = props;

  const styles = StyleSheet.create({
    container: {
      flex: (size) ? size : (containerStyle && containerStyle.width) ? 0 : 1,
      flexDirection: 'column',
    },
  });

  if (onPress) {
    return (
      <TouchableOpacity
        style={[
          styles.container,
          containerStyle && containerStyle,
        ]}
        activeOpacity={activeOpacity}
        onPress={onPress}
      >
        <View
          {...props}
        >
          {props.children}
        </View>
      </TouchableOpacity>
    );
  }

  return (
    <View
      style={[
        styles.container,
        containerStyle && containerStyle,
      ]}
      {...props}
    >
      {props.children}
    </View>
  );
};

Col.propTypes = {
  size: PropTypes.number,
  containerStyle: PropTypes.any,
  onPress: PropTypes.func,
  activeOpacity: PropTypes.number,
  children: PropTypes.any,
};

Col.defaultProps = {
  activeOpacity: 1,
};

export default Col;

import React, { Component, PropTypes } from 'react';
import { View, StyleSheet, TouchableOpacity } from 'react-native';
import Row from './Row';

class Grid extends Component {
  styles = StyleSheet.create({
    container: {
      flex: 1,
      flexDirection: this.isRow() ? 'column' : 'row',
    },
  })

  isRow() {
    let isRow = false;
    React.Children.forEach(this.props.children, (child) => {
      if (child && child.type === Row) {
        isRow = true;
      }
    });

    return isRow;
  }

  render() {
    const {onPress, activeOpacity, containerStyle} = this.props;

    if (onPress) {
      return (
        <TouchableOpacity activeOpacity={activeOpacity} onPress={onPress}>
          <View
            style={[
              this.styles.container,
              containerStyle && containerStyle,
            ]}
            {...this.props}
          >
            {this.props.children}
          </View>
        </TouchableOpacity>
      );
    }

    return (
      <View
        style={[
          this.styles.container,
          containerStyle && containerStyle,
        ]}
        {...this.props}
      >
        {this.props.children}
      </View>
    );
  }
}

Grid.propTypes = {
  containerStyle: PropTypes.any,
  onPress: PropTypes.func,
  activeOpacity: PropTypes.number,
  children: PropTypes.any,
};

Grid.defaultProps = {
  activeOpacity: 1,
};

export default Grid;

import React, { PropTypes } from 'react';
import { View, StyleSheet, TouchableOpacity } from 'react-native';

const Row = (props) => {
  const {containerStyle, size, onPress, activeOpacity} = props;

  const styles = StyleSheet.create({
    container: {
      flex: (size) ? size : (containerStyle && containerStyle.height) ? 0 : 1,
